import { useState } from "react";
import { loginAdmin } from "../services/api";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false); // Track the loading state
  const [error, setError] = useState(""); // Track error messages
  const navigate = useNavigate();

  // ‚úÖ Handle input changes and reset error message
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError(""); // Reset error when user types
  };

  // ‚úÖ Handle form submission (updated function)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Start loading
    setError(""); // Reset previous error

    try {
      console.log("üîπ Attempting login with:", formData);
      const response = await loginAdmin(formData);

      console.log("‚úÖ Full API Response:", response); // üîπ Debugging step

      if (!response || !response.token) {
        console.log("‚ùå Login failed: No token returned");
        setError("Invalid response from server. Please try again.");
        return;
      }

      console.log("‚úÖ Login successful, token received");
      localStorage.setItem("adminToken", response.token);
      navigate("/admin/dashboard"); // Redirect to dashboard
    } catch (error) {
      console.error("‚ùå Login error:", error);

      // ‚úÖ Show proper error message
      setError(
        error.response?.data?.message ||
        error.response?.data ||
        "Invalid credentials. Please try again."
      );
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-blue-600 mb-4 text-center">Admin Login</h2>

      {/* Display error message */}
      {error && <p className="text-red-600 text-center bg-red-100 p-2 rounded mb-3">{error}</p>}

      <form onSubmit={handleSubmit} className="space-y-4">
        <input 
          type="email" 
          name="email" 
          placeholder="Email" 
          value={formData.email} 
          onChange={handleChange} 
          className="w-full p-2 border rounded" 
          required 
        />
        <input 
          type="password" 
          name="password" 
          placeholder="Password" 
          value={formData.password} 
          onChange={handleChange} 
          className="w-full p-2 border rounded" 
          required 
        />
        
        <button 
          type="submit" 
          className={`bg-blue-600 text-white p-2 w-full rounded ${loading ? "opacity-50 cursor-not-allowed" : ""}`} 
          disabled={loading} // Disable button when loading
        >
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
};

export default Login;
