import axios from "axios";

// Create an instance of axios with the base URL
const API = axios.create({ baseURL: "http://localhost:5000/api" });

// Add an interceptor to include the Authorization token in every request if it's available
API.interceptors.request.use(
  (config) => {
    // Skip attaching the Authorization header for the login request
    if (config.url !== "/admin/login") {
      const token = localStorage.getItem("adminToken"); // Get the token from localStorage
      if (token) {
        config.headers["Authorization"] = `Bearer ${token}`; // Attach token to headers if available
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Login Admin
export const loginAdmin = async (data) => {
  try {
    const response = await API.post("/admin/login", data); // Send the login request
    return response.data; // Return the response if successful
  } catch (error) {
    console.error("Error logging in:", error.response ? error.response.data : error.message);
    throw error; // Propagate the error for further handling
  }
};

// Get List of Students
export const getStudents = () => API.get("/students");

// Register a Student
export const registerStudent = (data) => API.post("/students/register", data);

// Approve a Student
export const approveStudent = async (id) => {
  try {
    const response = await API.put(`/admin/approve/${id}`);
    return response.data; // Return the response if successful
  } catch (error) {
    console.error("Error approving student:", error);
    throw error; // Propagate error for further handling
  }
};

// Reject a Student
export const rejectStudent = async (id) => {
  try {
    const response = await API.put(`/admin/reject/${id}`);
    return response.data; // Return the response if successful
  } catch (error) {
    console.error("Error rejecting student:", error);
    throw error; // Propagate error for further handling
  }
};
