import { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import QRScanner from 'qr-scanner'; // Import the QRScanner library
import { verifyQRCode } from '../services/api'; // Import the verifyQRCode function from API

const QRScannerComponent = () => {
  const [message, setMessage] = useState(null);
  const [cameraFacing, setCameraFacing] = useState('environment'); // Default to back camera
  const [scannedQRCode, setScannedQRCode] = useState(null); // Store scanned QR code
  const [isScanning, setIsScanning] = useState(false); // Control the scanning process
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const qrScannerRef = useRef(null); // To hold QRScanner instance

  useEffect(() => {
    if (videoRef.current) {
      qrScannerRef.current = new QRScanner(videoRef.current, handleScan);

      const constraints = {
        video: {
          facingMode: cameraFacing, // Default facing mode
          width: { ideal: 1280 },
          height: { ideal: 720 },
        },
      };

      // Start scanning if the camera feed is available
      navigator.mediaDevices
        .getUserMedia(constraints)
        .then((stream) => {
          if (videoRef.current) {
            videoRef.current.srcObject = stream;
            qrScannerRef.current.start(); // Start scanning
          }
        })
        .catch((error) => {
          setMessage({ type: 'error', text: 'Camera access denied or unavailable!' });
          console.error(error);
        });

      return () => {
        const stream = videoRef.current?.srcObject;
        if (stream) {
          const tracks = stream.getTracks();
          tracks.forEach((track) => track.stop()); // Stop the camera feed
        }
      };
    }
  }, [cameraFacing]);

  // Handle QR Code Scan
  const handleScan = async (qrCode) => {
    if (qrCode) {
      console.log('Scanned QR Code:', qrCode);

      // Check if the scanned QR code is a duplicate
      if (qrCode === scannedQRCode) {
        setMessage({ type: 'error', text: 'Duplicate QR Code!' });
        return; // Prevent further scanning if duplicate
      }

      setScannedQRCode(qrCode); // Store the scanned QR code

      try {
        // Send the QR code to the backend to verify using the verifyQRCode function
        const response = await verifyQRCode(qrCode);

        // Check backend response for duplicate or success
        if (response.success) {
          setMessage({ type: 'success', text: 'QR Code Scanned Successfully!' });
        } else if (response.message === 'QR Code already scanned') {
          setMessage({ type: 'error', text: 'QR Code has already been scanned or is already associated with a student.' });
        } else {
          setMessage({ type: 'error', text: 'Error verifying QR code!' });
        }

        // Auto-clear success/error message after 3 seconds
        setTimeout(() => {
          setMessage(null);
        }, 3000);
      } catch (error) {
        setMessage({
          type: 'error',
          text: error.response?.data?.message || 'Error verifying QR code!',
        });

        // Auto-clear error message after 3 seconds
        setTimeout(() => {
          setMessage(null);
        }, 3000);
      }
    }
  };

  // Handle Camera Errors
  const handleError = (error) => {
    console.error('Camera Error:', error);
    setMessage({ type: 'error', text: 'Camera access denied or unavailable!' });

    // Auto-clear error message after 3 seconds
    setTimeout(() => {
      setMessage(null);
    }, 3000);
  };

  // Start the scanning process by toggling isScanning state
  const startScan = () => {
    setIsScanning(true);
    qrScannerRef.current.start(); // Start the QR scanner
  };

  // Stop scanning
  const stopScan = () => {
    setIsScanning(false);
    qrScannerRef.current.stop(); // Stop the QR scanner
  };

  // Reset the scanning state after successful scan and show the "Next Scan" button
  const handleNextScan = () => {
    setScannedQRCode(null); // Clear the previously scanned QR code
    setMessage(null); // Clear any existing messages
    startScan(); // Start the scanning process again
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      {/* Modal Container */}
      <div className="bg-white shadow-lg p-6 rounded-lg w-full max-w-md relative">
        <h2 className="text-2xl font-bold text-blue-600 mb-4 text-center">QR Code Scanner</h2>

        {/* Status Message */}
        {message && (
          <p
            className={`p-3 text-center rounded-md text-white ${message.type === 'error' ? 'bg-red-500' : 'bg-green-500'}`}
          >
            {message.text}
          </p>
        )}

        {/* QR Scanner Video Feed */}
        <div
          className="scanner-container mt-4 bg-gray-200 rounded-md overflow-hidden"
          style={{ position: 'relative', width: '100%', height: '300px' }}
        >
          {/* Video element to show the camera feed */}
          <video
            ref={videoRef}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
            }}
            autoPlay
            muted
          ></video>
        </div>

        {/* Control Buttons */}
        <div className="flex flex-col gap-3 mt-4">
          {!isScanning ? (
            <button
              onClick={startScan}
              className="bg-blue-500 text-white p-2 rounded-md text-center shadow-md hover:bg-blue-600 transition-all"
            >
              Start Scanning
            </button>
          ) : (
            <button
              onClick={stopScan}
              className="bg-red-500 text-white p-2 rounded-md text-center shadow-md hover:bg-red-600 transition-all"
            >
              Stop Scanning
            </button>
          )}

          {/* Show "Next Scan" button after a successful scan */}
          {scannedQRCode && message?.type === 'success' && (
            <button
              onClick={handleNextScan}
              className="bg-green-500 text-white p-2 rounded-md text-center shadow-md hover:bg-green-600 transition-all"
            >
              Scan Another QR Code
            </button>
          )}

          <button
            onClick={() => navigate('/admin/dashboard')}
            className="bg-gray-600 text-white p-2 rounded-md text-center shadow-md hover:bg-gray-700 transition-all"
          >
            Go Back to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default QRScannerComponent;
