import React, { useState } from "react";
import { registerStudent } from "../services/api";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    rollNo: "",
    branch: "",
    email: "",
    phone: "",
    studentID: null,
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, studentID: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
  
    const formDataToSend = new FormData();
    formDataToSend.append("name", formData.name);
    formDataToSend.append("rollNo", formData.rollNo);
    formDataToSend.append("branch", formData.branch);
    formDataToSend.append("email", formData.email);
    formDataToSend.append("phone", formData.phone);
    if (formData.studentID) {
      formDataToSend.append("studentIdCard", formData.studentID);
    }
  
    try {
      const response = await registerStudent(formDataToSend);
      console.log("‚úÖ API Response:", response); // üîπ Debugging log
  
      if (!response || !response.message) {
        throw new Error("Invalid response from server");
      }
  
      alert(response.message);
      setFormData({ name: "", rollNo: "", branch: "", email: "", phone: "", studentID: null });
    } catch (error) {
      console.error("‚ùå Registration Error:", error.response?.data || error.message);
      alert(error.response?.data?.message || "Error submitting application");
    } finally {
      setLoading(false);
    }
  };
  

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-6">Ticket Booking</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} className="w-full p-2 border" required />
          <input type="text" name="rollNo" placeholder="Roll No" value={formData.rollNo} onChange={handleChange} className="w-full p-2 border" required />
          <input type="text" name="branch" placeholder="Branch" value={formData.branch} onChange={handleChange} className="w-full p-2 border" required />
          <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} className="w-full p-2 border" required />
          <input type="text" name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} className="w-full p-2 border" required />
          <input type="file" accept="image/*" onChange={handleFileChange} className="w-full p-2 border" required />
          <button type="submit" className="bg-blue-600 text-white p-2 w-full" disabled={loading}>
            {loading ? "Submitting..." : "Submit"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
