import { useState } from "react";
import { loginAdmin } from "../services/api"; // Ensure correct path
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false); // Track the loading state
  const [error, setError] = useState(""); // Track error messages
  const navigate = useNavigate();

  // ‚úÖ Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError(""); // Clear error when user starts typing
  };

  // ‚úÖ Handle form submission (login attempt)
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    setLoading(true); // Start loading

    try {
      console.log("üîπ Attempting login with:", formData); // Debugging log
      const response = await loginAdmin(formData); // Send login request

      if (response?.token) {
        console.log("‚úÖ Login successful, token received");
        localStorage.setItem("adminToken", response.token); // Store token
        navigate("/admin/dashboard"); // Redirect to dashboard
      } else {
        console.log("‚ùå Login failed: No token returned");
        setError("Invalid response from server. Please try again.");
      }
    } catch (error) {
      console.error("‚ùå Login error: ", error);
      setError(error.response?.data?.message || "Invalid Credentials. Please try again."); // Set error message
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="max-w-md mx-auto mt-16 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-3xl font-bold text-blue-600 mb-4 text-center">Admin Login</h2>

      {/* Display error message */}
      {error && <p className="text-red-600 text-center bg-red-100 p-2 rounded mb-3">{error}</p>}

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Email input */}
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          value={formData.email}
          className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
          required
        />

        {/* Password input */}
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          value={formData.password}
          className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
          required
        />

        {/* Login button */}
        <button
          type="submit"
          className={`bg-blue-600 text-white p-2 w-full rounded transition-all duration-300 ${
            loading || !formData.email || !formData.password
              ? "opacity-50 cursor-not-allowed"
              : "hover:bg-blue-700"
          }`}
          disabled={loading || !formData.email || !formData.password} // Disable if loading or fields are empty
        >
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
};

export default Login;
