import { useState } from "react";
import { registerStudent } from "../services/api";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    rollNo: "",
    branch: "",
    email: "",
    phone: "",
    studentIdCard: null, // Keep the same field name
  });

  const [message, setMessage] = useState(null);

  // Handle text input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle file input correctly
  const handleFileChange = (e) => {
    setFormData({ ...formData, studentIdCard: e.target.files[0] }); // Fix field name
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);

    const formDataObj = new FormData();

    // Append only non-null values
    Object.entries(formData).forEach(([key, value]) => {
      if (value) formDataObj.append(key, value);
    });

    console.log("ðŸ”¹ FormData before sending:", [...formDataObj.entries()]); // Debugging

    try {
      const response = await registerStudent(formDataObj); // Pass the FormData object here
      setMessage({ type: "success", text: "Student registered successfully!" });
    } catch (error) {
      setMessage({ type: "error", text: error.response?.data?.message || "Error registering." });
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-lg mt-10">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">Student Registration</h2>

      {message && (
        <p className={`p-3 text-white ${message.type === "error" ? "bg-red-500" : "bg-green-500"}`}>
          {message.text}
        </p>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="rollNo"
          placeholder="Roll Number"
          value={formData.rollNo}
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          value={formData.phone}
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="branch"
          placeholder="Branch"
          value={formData.branch}
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="file"
          name="studentIdCard"
          onChange={handleFileChange}
          className="w-full p-2 border"
          required
        />
        <button type="submit" className="bg-blue-600 text-white p-2 w-full">
          Register
        </button>
      </form>
    </div>
  );
};

export default Register;
