import { useState } from "react";
import { registerStudent } from "../services/api";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    rollNo: "",
    branch: "",
    email: "",
    phone: "",
    studentIdCard: null,
  });

  const [message, setMessage] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, studentIdCard: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null); // Reset the message before submitting

    const formDataObj = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      formDataObj.append(key, value);
    });

    try {
      // Call the registerStudent API function
      const response = await registerStudent(formDataObj);

      // Log the response for debugging purposes
      console.log("Full response from server:", response);

      // Check if the response contains the status "success"
      if (response.status === "success") {
        setMessage({
          type: "success",
          text: response.message || "Student registered successfully.",
        });
      } else {
        setMessage({
          type: "error",
          text: response.message || "Unexpected error during registration.",
        });
      }
    } catch (error) {
      console.error("❌ Error during registration:", error);

      // Check if error.response exists and if it has the data.message property
      const errorMessage = error.response?.data?.message || "Error booking the ticket. Please try again later.";

      setMessage({
        type: "error",
        text: errorMessage,
      });
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-lg mt-10">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">Give Details to Book your Ticket</h2>

      {/* Show success or error message */}
      {message && (
        <p className={`p-3 text-white ${message.type === "error" ? "bg-red-500" : "bg-green-500"}`}>
          {message.text}
        </p>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Name"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="rollNo"
          placeholder="Roll Number"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="branch"
          placeholder="Branch"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone Number"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="file"
          name="studentIdCard"
          accept="image/*"
          onChange={handleFileChange}
          className="w-full p-2 border"
          required
        />
        <button type="submit" className="bg-blue-600 text-white p-2 w-full">
          Book Now
        </button>
      </form>
    </div>
  );
};

export default Register;
