import { useState } from "react";
import { loginAdmin } from "../services/api";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false); // To track the loading state
  const navigate = useNavigate();

  // Handle changes in the input fields
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  // Handle form submission (login attempt)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Set loading to true when the request starts

    try {
      const { data } = await loginAdmin(formData); // Make the login request
      localStorage.setItem("adminToken", data.token); // Store the JWT token in localStorage
      alert("Login Successful!");
      navigate("/admin/dashboard"); // Redirect to the dashboard
    } catch (error) {
      console.error("Login error: ", error);
      alert("Invalid Credentials. Please try again."); // Show alert if login fails
    } finally {
      setLoading(false); // Reset the loading state after the request
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">Admin Login</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Input field for email */}
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          value={formData.email}
          className="w-full p-2 border rounded"
          required
        />
        {/* Input field for password */}
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          value={formData.password}
          className="w-full p-2 border rounded"
          required
        />
        {/* Login button */}
        <button
          type="submit"
          className={`bg-blue-600 text-white p-2 w-full rounded ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
          disabled={loading} // Disable the button if loading
        >
          {loading ? "Logging in..." : "Login"} {/* Show loading text when logging in */}
        </button>
      </form>
    </div>
  );
};

export default Login;
