import { useState } from "react";
import { QrReader } from "react-qr-reader";
import axios from "axios";

const QRScanner = () => {
  const [result, setResult] = useState(null); // To store the result of the QR scan
  const [loading, setLoading] = useState(false); // To show a loading state
  const [errorMessage, setErrorMessage] = useState(""); // To show error messages
  const [successMessage, setSuccessMessage] = useState(""); // To show success message

  // Handle successful scan
  const handleScan = async (data) => {
    if (data && data !== result) { // Prevent unnecessary re-processing if result is the same
      setResult(data); // Update the result state with the scanned QR code
      setLoading(true); // Show loading state while processing the result
      setErrorMessage(""); // Clear previous errors
      setSuccessMessage(""); // Clear success message

      try {
        // Sending the scanned roll number to the backend API for validation
        const response = await axios.post("http://localhost:5000/api/verify-ticket", { rollNo: data });
        setLoading(false); // Hide loading after the API call
        if (response.data.success) {
          setSuccessMessage(response.data.message); // Show success message if validation succeeds
        } else {
          setErrorMessage("Invalid Ticket! Please try again."); // Show error if the ticket is invalid
        }
      } catch (error) {
        setLoading(false); // Hide loading if there's an error
        setErrorMessage("Error communicating with the server. Please try again."); // Show error message
      }
    }
  };

  // Handle error during scanning
  const handleError = (err) => {
    console.error(err); // Log error to the console
    setErrorMessage("Error scanning QR code. Please check your camera."); // Display error message
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg text-center">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">QR Code Scanner</h2>

      {/* QR Reader Component */}
      <div className="relative mb-4">
        <QrReader
          delay={300} // The delay time for scanning (in ms)
          onScan={handleScan} // Callback when QR code is successfully scanned
          onError={handleError} // Callback when an error occurs while scanning
          style={{ width: "100%", height: "auto", borderRadius: "8px", border: "2px solid #ddd" }} // Styling for QR reader
        />

        {/* Loading Overlay */}
        {loading && (
          <div className="absolute top-0 left-0 right-0 bottom-0 bg-black opacity-50 flex items-center justify-center text-white text-xl rounded-lg">
            Loading...
          </div>
        )}
      </div>

      {/* Scanned Result Display */}
      {result && !loading && !errorMessage && (
        <p className="mt-4 text-green-600 font-bold">Scanned Roll No: {result}</p>
      )}

      {/* Success Message Display */}
      {successMessage && (
        <p className="mt-4 text-green-600 font-bold">{successMessage}</p>
      )}

      {/* Error Message Display */}
      {errorMessage && (
        <p className="mt-4 text-red-600 font-bold">{errorMessage}</p>
      )}
    </div>
  );
};

export default QRScanner;
