import React, { useState } from "react";
import { QrReader } from "@blackbox-vision/react-qr-reader";
import axios from "axios"; // Replace with your axios instance
import { useNavigate } from "react-router-dom";

const QRScanner = () => {
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [scannedData, setScannedData] = useState(null);
  const [isScanningEnabled, setIsScanningEnabled] = useState(true);
  const navigate = useNavigate();

  const handleScan = (data) => {
    if (data && isScanningEnabled) {
      setScannedData(data);
      setError("");
      setSuccess("");
      setIsScanningEnabled(false);
    }
  };

  const handleError = (err) => {
    console.error(err);
    setError("An error occurred while scanning. Please try again.");
  };

  const handleSubmit = async () => {
    try {
      const response = await axios.put(
        `http://localhost:5000/api/verify-ticket/${scannedData}`
      );
      if (response.status === 200) {
        setSuccess("Ticket verified successfully!");
        setTimeout(() => {
          setSuccess("");
          resetScanner();
        }, 3000);
      }
    } catch (err) {
      console.log(err);
      if (err.response.status === 404) {
        setError("Ticket not found. Please check the QR code and try again.");
      } else {
        setError("Failed to verify the ticket. Please try again.");
      }
      setTimeout(() => {
        setError("");
        resetScanner();
      }, 3000);
    }
  };

  const resetScanner = () => {
    setScannedData(null);
    setIsScanningEnabled(true);
  };

  const handleBack = () => {
    navigate("/admin/dashboard"); // Navigate back to the dashboard
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center py-12 px-6">
      <div className="w-full max-w-md bg-white p-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold text-center text-gray-800 mb-6">
          QR Code Scanner
        </h2>

        {/* QR Code Scanner */}
        <div className="relative mb-4">
          <QrReader
            delay={500}
            onError={handleError}
            onResult={(result) => handleScan(result?.text)}
            constraints={{ facingMode: "environment" }}
            style={{ width: "100%", marginBottom: "20px" }}
          />
          {/* Error or Success Message */}
          {error && <p className="text-red-500 text-center mb-4">{error}</p>}
          {success && <p className="text-green-500 text-center mb-4">{success}</p>}
        </div>

        {/* Submit Button - Appears after QR Code is Scanned */}
        {scannedData && (
          <button
            onClick={handleSubmit}
            className="w-full py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          >
            Submit Ticket
          </button>
        )}

        {/* Back Button */}
        <button
          onClick={handleBack}
          className="w-full py-3 bg-gray-400 text-white font-semibold rounded-lg hover:bg-gray-500 focus:outline-none focus:ring-2 focus:ring-gray-400 mb-4"
        >
          Back to Dashboard
        </button>
      </div>
    </div>
  );
};

export default QRScanner;
