import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { QrReader } from "react-qr-reader"; // ✅ Using react-qr-reader for better compatibility
import axios from "axios";

const QRScanner = () => {
  const [message, setMessage] = useState(null);
  const [scannedCodes, setScannedCodes] = useState(new Set());
  const [scanning, setScanning] = useState(true);
  const navigate = useNavigate();

  // ✅ Handle QR Code Scan
  const handleScan = async (result) => {
    if (result?.text && scanning) {
      const qrCode = result.text;

      // ✅ Prevent duplicate scans
      if (scannedCodes.has(qrCode)) {
        setMessage({ type: "error", text: "This QR Code has already been scanned!" });
        return;
      }

      setScanning(false); // Pause scanning while verifying

      try {
        const response = await axios.post("http://localhost:5000/api/admin/verify", { qrCode });

        setMessage({ type: "success", text: response.data.message });
        setScannedCodes((prev) => new Set(prev.add(qrCode)));

        setTimeout(() => {
          setScanning(true); // Resume scanning after a short delay
        }, 3000);
      } catch (error) {
        setMessage({
          type: "error",
          text: error.response?.data?.message || "Error verifying QR code!",
        });

        setTimeout(() => {
          setScanning(true);
        }, 3000);
      }
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <div className="bg-white shadow-lg p-6 rounded-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-blue-600 mb-4 text-center">QR Code Scanner</h2>

        {/* ✅ Status Message */}
        {message && (
          <p
            className={`p-3 text-center rounded-md text-white ${
              message.type === "error" ? "bg-red-500" : "bg-green-500"
            }`}
          >
            {message.text}
          </p>
        )}

        {/* ✅ QR Scanner */}
        <div className="mt-4 bg-gray-200 rounded-md overflow-hidden">
          <QrReader
            constraints={{ facingMode: "environment" }} // Use back camera
            onResult={handleScan}
            scanDelay={500}
            style={{ width: "100%" }}
          />
        </div>

        {/* ✅ Buttons */}
        <div className="flex flex-col gap-3 mt-4">
          <button
            onClick={() => setScanning(!scanning)}
            className="bg-blue-600 text-white p-2 rounded-md text-center shadow-md hover:bg-blue-700 transition-all"
          >
            {scanning ? "Pause Scanner" : "Resume Scanner"}
          </button>

          <button
            onClick={() => navigate("/admin/dashboard")}
            className="bg-gray-600 text-white p-2 rounded-md text-center shadow-md hover:bg-gray-700 transition-all"
          >
            Go Back to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default QRScanner;
