import { useEffect, useState } from "react";
import { getStudents, approveStudent, rejectStudent } from "../services/api"; // Ensure rejectStudent is implemented

const AdminDashboard = () => {
  const [students, setStudents] = useState([]);
  const [selectedIdCard, setSelectedIdCard] = useState(null);
  const token = localStorage.getItem("adminToken");

  useEffect(() => {
    const fetchStudents = async () => {
      const { data } = await getStudents();
      setStudents(data);
    };
    fetchStudents();
  }, []);

  const handleApprove = async (id) => {
    await approveStudent(id, token);
    alert("Student Approved & QR Code Sent!");
    setStudents((prevStudents) =>
      prevStudents.map((student) =>
        student._id === id ? { ...student, approved: true } : student
      )
    );
  };

  const handleReject = async (id) => {
    try {
      await rejectStudent(id, token);
      alert("Student Rejected!");
      setStudents((prevStudents) =>
        prevStudents.filter((student) => student._id !== id)
      );
    } catch (error) {
      console.error("Error rejecting student: ", error);

      // Check if the error response is from the backend rejection check
      if (error.response && error.response.status === 400) {
        alert("This student has already been rejected.");
      } else {
        alert("An error occurred while rejecting the student.");
      }
    }
  };

  const handleViewIdCard = (studentIdCard) => {
    setSelectedIdCard(studentIdCard);
  };

  const handleCloseModal = () => {
    setSelectedIdCard(null);
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Admin Dashboard</h2>

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded mt-4">
          <thead>
            <tr className="bg-blue-600 text-white">
              <th className="p-2 text-xs md:text-base">Name</th>
              <th className="p-2 text-xs md:text-base">Roll No</th>
              <th className="p-2 text-xs md:text-base">Phone</th>
              <th className="p-2 text-xs md:text-base">ID Card</th>
              <th className="p-2 text-xs md:text-base">Status</th>
              <th className="p-2 text-xs md:text-base">Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student._id} className="border-b">
                <td className="p-2 text-sm md:text-base">{student.name}</td>
                <td className="p-2 text-sm md:text-base">{student.rollNo}</td>
                <td className="p-2 text-sm md:text-base">{student.phone}</td>
                <td className="p-2">
                  {student.studentIdCard ? (
                    <button
                      onClick={() => handleViewIdCard(student.studentIdCard)}
                      className="text-blue-500 hover:underline text-xs md:text-base"
                    >
                      View ID Card
                    </button>
                  ) : (
                    <span className="text-sm md:text-base">No ID Card</span>
                  )}
                </td>
                <td className="p-2 text-sm md:text-base">
                  {student.approved ? "Approved" : "Pending"}
                </td>
                <td className="p-2">
                  {!student.approved ? (
                    <>
                      <button
                        onClick={() => handleApprove(student._id)}
                        className="bg-green-500 text-white p-1 rounded mr-2 text-xs md:text-base"
                      >
                        Approve
                      </button>
                      <button
                        onClick={() => handleReject(student._id)}
                        className="bg-red-500 text-white p-1 rounded text-xs md:text-base"
                      >
                        Reject
                      </button>
                    </>
                  ) : (
                    <span className="text-sm md:text-base">Approved</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal for Viewing ID Card */}
      {selectedIdCard && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-4 rounded shadow-lg max-w-full sm:max-w-md w-full">
            <h3 className="text-lg font-semibold mb-4">Student ID Card</h3>
            <img
              src={`http://localhost:5000${selectedIdCard}`}
              alt="Student ID Card"
              className="max-w-full max-h-96 object-contain mb-4"
            />
            <button
              onClick={handleCloseModal}
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded w-full sm:w-auto"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
