import { useEffect, useState } from "react";
import { getStudents, approveStudent, rejectStudent } from "../services/api"; // Ensure rejectStudent is implemented

const AdminDashboard = () => {
  const [students, setStudents] = useState([]); // Ensure students state is initialized as an empty array
  const [selectedIdCard, setSelectedIdCard] = useState(null);
  const [loading, setLoading] = useState(true); // Track loading state
  const [error, setError] = useState(null); // Track error state
  const token = localStorage.getItem("adminToken");

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const { data } = await getStudents();
        setStudents(data);
        setLoading(false); // Set loading to false after data is fetched
      } catch (err) {
        setError("Failed to fetch students");
        setLoading(false); // Set loading to false even if there's an error
      }
    };
    fetchStudents();
  }, []);

  const handleApprove = async (id) => {
    await approveStudent(id, token);
    alert("Student Approved & QR Code Sent!");
    setStudents((prevStudents) =>
      prevStudents.map((student) =>
        student._id === id ? { ...student, approved: true } : student
      )
    );
  };

  const handleReject = async (id) => {
    await rejectStudent(id, token); // Add rejectStudent in your API services
    alert("Student Rejected!");
    setStudents((prevStudents) =>
      prevStudents.filter((student) => student._id !== id)
    );
  };

  const handleViewIdCard = (studentIdCard) => {
    setSelectedIdCard(studentIdCard);
  };

  const handleCloseModal = () => {
    setSelectedIdCard(null);
  };

  // Loading and error handling
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold">Admin Dashboard</h2>
      <table className="min-w-full bg-white shadow-md rounded mt-4">
        <thead>
          <tr className="bg-blue-600 text-white">
            <th className="p-2">Name</th>
            <th className="p-2">Roll No</th>
            <th className="p-2">Phone</th>
            <th className="p-2">ID Card</th>
            <th className="p-2">Status</th>
            <th className="p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.length > 0 ? (
            students.map((student) => (
              <tr key={student._id} className="border-b">
                <td className="p-2">{student.name}</td>
                <td className="p-2">{student.rollNo}</td>
                <td className="p-2">{student.phone}</td>
                <td className="p-2">
                  {student.studentIdCard ? (
                    <button
                      onClick={() => handleViewIdCard(student.studentIdCard)}
                      className="text-blue-500 hover:underline"
                    >
                      View ID Card
                    </button>
                  ) : (
                    "No ID Card"
                  )}
                </td>
                <td className="p-2">{student.approved ? "Approved" : "Pending"}</td>
                <td className="p-2">
                  {!student.approved ? (
                    <>
                      <button
                        onClick={() => handleApprove(student._id)}
                        className="bg-green-500 text-white p-1 rounded mr-2"
                      >
                        Approve
                      </button>
                      <button
                        onClick={() => handleReject(student._id)}
                        className="bg-red-500 text-white p-1 rounded"
                      >
                        Reject
                      </button>
                    </>
                  ) : (
                    "Approved"
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6" className="text-center p-2">No students available</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Modal for Viewing ID Card */}
      {selectedIdCard && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-4 rounded shadow-lg max-w-lg w-full">
            <h3 className="text-lg font-semibold mb-4">Student ID Card</h3>
            <img
              src={`http://localhost:5000/uploads/${selectedIdCard}`}
              alt="Student ID Card"
              className="max-w-full max-h-96 object-contain"
            />
            <button
              onClick={handleCloseModal}
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
