import { useState, useEffect } from "react";
import { BrowserMultiFormatReader } from "@zxing/browser";

const QRScanner = () => {
  const [qrData, setQrData] = useState(null);
  const [error, setError] = useState(null);
  let codeReader = new BrowserMultiFormatReader();
  let videoElement = null; // Reference for the video element

  useEffect(() => {
    const startScanner = async () => {
      try {
        videoElement = document.getElementById("video");
        const devices = await navigator.mediaDevices.enumerateDevices();
        const cameras = devices.filter((device) => device.kind === "videoinput");

        // Pick the rear camera if available
        const cameraId = cameras.length > 1 ? cameras[1].deviceId : cameras[0]?.deviceId;

        if (cameraId) {
          await codeReader.decodeFromVideoDevice(cameraId, videoElement, (result, err) => {
            if (result) {
              setQrData(result.getText());
              console.log("QR Code Data:", result.getText());
            }
            if (err) {
              setError(err.message);
            }
          });
        } else {
          setError("No camera found");
        }
      } catch (err) {
        setError(err.message);
      }
    };

    startScanner();

    return () => {
      if (videoElement?.srcObject) {
        let stream = videoElement.srcObject;
        let tracks = stream.getTracks();
        tracks.forEach((track) => track.stop()); // Stop the camera stream properly
        videoElement.srcObject = null;
      }
    };
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">QR Code Scanner</h1>
      <video id="video" className="w-80 h-80 bg-black shadow-lg rounded-md" />
      {qrData && (
        <p className="mt-4 p-2 bg-green-200 text-green-800 rounded-md">
          Scanned Data: {qrData}
        </p>
      )}
      {error && <p className="text-red-500 mt-2">{error}</p>}
    </div>
  );
};

export default QRScanner;
