import axios from 'axios';

const API_URL = "http://localhost:5000/api"; // Replace with your backend URL

// Create an Axios instance for easier configuration
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Helper function to set authorization token in headers
const setAuthHeader = (token) => {
  api.defaults.headers['Authorization'] = `Bearer ${token}`;
};

// Register a new student
export const registerStudent = async (studentData, studentIdCardFile) => {
  const formData = new FormData();
  formData.append('name', studentData.name);
  formData.append('rollNo', studentData.rollNo);
  formData.append('branch', studentData.branch);
  formData.append('email', studentData.email);
  formData.append('phone', studentData.phone);
  formData.append('studentIdCard', studentIdCardFile); // Image file

  try {
    const response = await api.post('/students/register', formData, {
      headers: {
        'Content-Type': 'multipart/form-data', // Handle file upload
      },
    });
    return response.data;
  } catch (error) {
    console.error("Error registering student:", error.response?.data || error.message);
    throw error;
  }
};

// Get all students (For Admin)
export const getAllStudents = async (token) => {
  try {
    setAuthHeader(token); // Set auth token in headers
    const response = await api.get('/students');
    return response.data;
  } catch (error) {
    console.error("Error fetching students:", error.response?.data || error.message);
    throw error;
  }
};

// Export getStudents alias for backwards compatibility
export const getStudents = getAllStudents; // Alias for backwards compatibility

// Get student by ID (For Admin)
export const getStudentById = async (studentId, token) => {
  try {
    setAuthHeader(token); // Set auth token in headers
    const response = await api.get(`/students/${studentId}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching student by ID:", error.response?.data || error.message);
    throw error;
  }
};

// Approve a student (For Admin)
export const approveStudent = async (studentId, token) => {
  try {
    setAuthHeader(token); // Set auth token in headers
    const response = await api.put(`/students/approve/${studentId}`);
    return response.data;
  } catch (error) {
    console.error("Error approving student:", error.response?.data || error.message);
    throw error;
  }
};

// Reject a student (For Admin)
export const rejectStudent = async (studentId, token) => {
  try {
    setAuthHeader(token); // Set auth token in headers
    const response = await api.put(`/students/reject/${studentId}`);
    return response.data;
  } catch (error) {
    console.error("Error rejecting student:", error.response?.data || error.message);
    throw error;
  }
};

// Verify QR code (For Admin)
export const verifyQRCode = async (qrCodeData, token) => {
  try {
    setAuthHeader(token); // Set auth token in headers
    const response = await api.post('/students/verify', { qrCodeData });
    return response.data;
  } catch (error) {
    console.error("Error verifying QR code:", error.response?.data || error.message);
    throw error;
  }
};

// Generate QR Code for a student (For Admin)
export const generateStudentQR = async (studentId, token) => {
  try {
    setAuthHeader(token); // Set auth token in headers
    const response = await api.post(`/students/generateQR/${studentId}`);
    return response.data;
  } catch (error) {
    console.error("Error generating QR code:", error.response?.data || error.message);
    throw error;
  }
};

// Admin login (JWT token generation)
export const adminLogin = async (email, password) => {
  try {
    const response = await api.post('/admin/login', { email, password });
    return response.data;
  } catch (error) {
    console.error("Error logging in admin:", error.response?.data || error.message);
    throw error;
  }
};

// Alias adminLogin as loginAdmin for frontend compatibility
export const loginAdmin = adminLogin; // Alias for frontend usage

// Get admin data (For Admin)
export const getAdminData = async (token) => {
  try {
    setAuthHeader(token); // Set auth token in headers
    const response = await api.get('/admin/data');
    return response.data;
  } catch (error) {
    console.error("Error fetching admin data:", error.response?.data || error.message);
    throw error;
  }
};
