import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getStudents, approveStudent, rejectStudent } from "../services/api"; // Ensure correct API imports

const AdminDashboard = () => {
  const [students, setStudents] = useState([]); // âœ… Ensure students always start as an array
  const [selectedIdCard, setSelectedIdCard] = useState(null);
  const [error, setError] = useState(""); // Track errors
  const navigate = useNavigate();

  // âœ… Redirect to login if token is missing
  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) {
      navigate("/admin/login");
    }
  }, [navigate]);

  // âœ… Fetch students when component loads
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        console.log("ðŸ”¹ Fetching students...");
        const response = await getStudents();
        console.log("âœ… Students data:", response);

        if (!Array.isArray(response)) {
          throw new Error("Invalid students data format");
        }

        setStudents(response); // âœ… Ensure students are set correctly
      } catch (err) {
        console.error("âŒ Error fetching students:", err);
        setError("Failed to load students. Please try again.");
      }
    };

    fetchStudents();
  }, []);

  // âœ… Approve student
  const handleApprove = async (id) => {
    try {
      await approveStudent(id);
      alert("Student Approved & QR Code Sent!");
      setStudents((prevStudents) =>
        prevStudents.map((student) =>
          student._id === id ? { ...student, approved: true, rejected: false } : student
        )
      );
    } catch (error) {
      console.error("âŒ Error approving student:", error);
      alert("An error occurred while approving the student.");
    }
  };

  // âœ… Reject student
  const handleReject = async (id) => {
    try {
      const student = students.find((s) => s._id === id);

      if (student && student.approved === false) {
        alert("This student has already been rejected.");
        return; // Stop execution if already rejected
      }

      await rejectStudent(id);
      alert("Student Rejected!");

      setStudents((prevStudents) =>
        prevStudents.map((student) =>
          student._id === id ? { ...student, approved: false, rejected: true } : student
        )
      );
    } catch (error) {
      console.error("âŒ Error rejecting student:", error);
      if (error.response && error.response.status === 400) {
        alert("This student has already been rejected.");
      } else {
        alert("An error occurred while rejecting the student.");
      }
    }
  };

  // âœ… Logout function
  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    navigate("/admin/login");
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Admin Dashboard</h2>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      {error && <p className="text-red-500 text-center">{error}</p>}

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded">
          <thead>
            <tr className="bg-blue-600 text-white">
              <th className="p-2">Name</th>
              <th className="p-2">Roll No</th>
              <th className="p-2">Phone</th>
              <th className="p-2">ID Card</th>
              <th className="p-2">Status</th>
              <th className="p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.length > 0 ? (
              students.map((student) => (
                <tr key={student._id} className="border-b">
                  <td className="p-2">{student.name}</td>
                  <td className="p-2">{student.rollNo}</td>
                  <td className="p-2">{student.phone}</td>
                  <td className="p-2">
                    {student.studentIdCard ? (
                      <button
                        onClick={() => setSelectedIdCard(student.studentIdCard)}
                        className="text-blue-500 hover:underline"
                      >
                        View ID Card
                      </button>
                    ) : (
                      <span>No ID Card</span>
                    )}
                  </td>
                  <td className="p-2">
                    {student.approved ? (
                      <span className="text-green-600 font-bold">Approved</span>
                    ) : student.rejected ? (
                      <span className="text-red-600 font-bold">Rejected</span>
                    ) : (
                      <span className="text-yellow-500 font-bold">Pending</span>
                    )}
                  </td>
                  <td className="p-2">
                    {!student.approved && !student.rejected ? (
                      <>
                        <button
                          onClick={() => handleApprove(student._id)}
                          className="bg-green-500 text-white p-1 rounded mr-2"
                        >
                          Approve
                        </button>
                        <button
                          onClick={() => handleReject(student._id)}
                          className="bg-red-500 text-white p-1 rounded"
                        >
                          Reject
                        </button>
                      </>
                    ) : (
                      <span>-</span>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="p-4 text-center text-gray-500">
                  No students found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* âœ… Modal for Viewing ID Card */}
      {selectedIdCard && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-4 rounded shadow-lg max-w-md">
            <h3 className="text-lg font-semibold mb-4">Student ID Card</h3>
            <img
              src={`http://localhost:5000${selectedIdCard}`}
              alt="Student ID Card"
              className="max-w-full max-h-96 object-contain mb-4"
            />
            <button
              onClick={() => setSelectedIdCard(null)}
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded w-full"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
