import { useState } from "react";
import { QrReader } from "react-qr-reader";
import axios from "axios";

const QRScanner = () => {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  // Handle successful scan
  const handleScan = async (data) => {
    if (data) {
      setResult(data);
      setLoading(true);
      try {
        // Send the roll number to your backend API
        const response = await axios.post("http://localhost:5000/api/verify-ticket", { rollNo: data });
        setLoading(false);
        alert(response.data.message);
      } catch (error) {
        setLoading(false);
        setErrorMessage("Invalid Ticket! Please try again.");
      }
    }
  };

  // Handle error during scanning
  const handleError = (err) => {
    console.error(err);
    setErrorMessage("Error scanning QR code. Please check your camera.");
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg text-center">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">QR Code Scanner</h2>

      {/* QRReader Component */}
      <div className="relative">
        <QrReader
          delay={300}
          onScan={handleScan}
          onError={handleError}
          style={{ width: "100%", height: "auto", borderRadius: "8px", border: "2px solid #ddd" }}
        />
        
        {/* Loading Overlay */}
        {loading && (
          <div className="absolute top-0 left-0 right-0 bottom-0 bg-black opacity-50 flex items-center justify-center text-white text-xl">
            Loading...
          </div>
        )}
      </div>

      {/* Scanned Result Display */}
      {result && (
        <p className="mt-4 text-green-600 font-bold">Scanned Roll No: {result}</p>
      )}

      {/* Error Message */}
      {errorMessage && (
        <p className="mt-4 text-red-600 font-bold">{errorMessage}</p>
      )}
    </div>
  );
};

export default QRScanner;
