import axios from "axios";

// Create an instance of axios with the base URL
const API = axios.create({ baseURL: "http://localhost:5000/api" });

// Add an interceptor to include the Authorization token in every request if it's available
API.interceptors.request.use(
  (config) => {
    // Skip attaching the Authorization header for the login request
    if (config.url !== "/admin/login") {
      const token = localStorage.getItem("adminToken"); // Get token from localStorage
      if (token) {
        config.headers["Authorization"] = `Bearer ${token}`; // Attach token if available
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// ‚úÖ Login Admin - Improved debugging
export const loginAdmin = async (data) => {
  try {
    console.log("üîπ Sending login request:", data); // Debugging
    const response = await API.post("/admin/login", data); // Send the login request
    console.log("‚úÖ Login successful:", response.data); // Debugging

    // Store the token if login is successful
    if (response.data.token) {
      localStorage.setItem("adminToken", response.data.token);
    }
    
    return response.data; // Return the response
  } catch (error) {
    console.error("‚ùå Login failed:", error.response?.data || error.message); // Improved error handling
    throw error; // Propagate error for handling
  }
};

// ‚úÖ Get List of Students - Ensure correct endpoint
export const getStudents = async () => {
  try {
    const response = await API.get("/students");
    return response.data;
  } catch (error) {
    console.error("‚ùå Error fetching students:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Register a Student
export const registerStudent = async (data) => {
  try {
    const response = await API.post("/students/register", data);
    return response.data;
  } catch (error) {
    console.error("‚ùå Error registering student:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Approve a Student
export const approveStudent = async (id) => {
  try {
    const response = await API.put(`/admin/approve/${id}`);
    return response.data;
  } catch (error) {
    console.error("‚ùå Error approving student:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Reject a Student
export const rejectStudent = async (id) => {
  try {
    const response = await API.put(`/admin/reject/${id}`);
    return response.data;
  } catch (error) {
    console.error("‚ùå Error rejecting student:", error.response?.data || error.message);
    throw error;
  }
};
