import { useEffect, useState } from "react";
import { getStudents, approveStudent } from "../services/api";

const AdminDashboard = () => {
  const [students, setStudents] = useState([]);
  const token = localStorage.getItem("adminToken");

  useEffect(() => {
    const fetchStudents = async () => {
      const { data } = await getStudents();
      setStudents(data);
    };
    fetchStudents();
  }, []);

  const handleApprove = async (id) => {
    await approveStudent(id, token);
    alert("Student Approved & QR Code Sent!");
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold">Admin Dashboard</h2>
      <table className="min-w-full bg-white shadow-md rounded mt-4">
        <thead>
          <tr className="bg-blue-600 text-white">
            <th className="p-2">Name</th>
            <th className="p-2">Roll No</th>
            <th className="p-2">Approve</th>
          </tr>
        </thead>
        <tbody>
          {students.map(student => (
            <tr key={student._id} className="border-b">
              <td className="p-2">{student.name}</td>
              <td className="p-2">{student.rollNo}</td>
              <td className="p-2">
                {!student.approved ? <button onClick={() => handleApprove(student._id)} className="bg-green-500 text-white p-1 rounded">Approve</button> : "Approved"}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
