import { useState } from "react";
import { QrReader } from "react-qr-reader";
import axios from "axios";

const QRScanner = () => {
  const [result, setResult] = useState(null);

  const handleScan = async (data) => {
    if (data) {
      setResult(data);
      try {
        const response = await axios.post("http://localhost:5000/api/verify-ticket", { rollNo: data });
        alert(response.data.message);
      } catch (error) {
        alert("Invalid Ticket!");
      }
    }
  };

  const handleError = (err) => console.error(err);

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg text-center">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">QR Code Scanner</h2>
      <QrReader delay={300} onScan={handleScan} onError={handleError} style={{ width: "100%" }} />
      {result && <p className="mt-4 text-green-600 font-bold">Scanned Roll No: {result}</p>}
    </div>
  );
};

export default QRScanner;
