import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { QrReader } from "react-qr-reader"; // Correct import for QR reader

const QRScanner = () => {
  const [error, setError] = useState(""); // Error message state
  const [success, setSuccess] = useState(""); // Success message state
  const [scannedData, setScannedData] = useState(null); // Store scanned QR data
  const [isScanningEnabled, setIsScanningEnabled] = useState(true); // Control scanning state
  const [isFrontCamera, setIsFrontCamera] = useState(false); // Handle front/back camera
  const navigate = useNavigate();
  
  // Use Ref to hold video element
  const videoRef = useRef(null);

  // Start the camera stream with constraints
  const startCamera = (facingMode) => {
    const constraints = {
      video: {
        facingMode: facingMode, // Choose between user (front) and environment (back) camera
        width: { ideal: 1280 },
        height: { ideal: 720 },
      },
    };

    navigator.mediaDevices
      .getUserMedia(constraints)
      .then((stream) => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      })
      .catch((err) => {
        console.error("Error accessing camera: ", err);
        setError("Unable to access camera. Please check your permissions.");
      });
  };

  // Switch the camera between front and back
  const toggleCamera = () => {
    const newCamera = isFrontCamera ? "environment" : "user"; // Toggle between front and back camera
    setIsFrontCamera(!isFrontCamera); // Toggle the camera state
    startCamera(newCamera); // Restart the camera with new facing mode
  };

  // Start camera on component mount
  useEffect(() => {
    startCamera(isFrontCamera ? "user" : "environment"); // Default to front camera
    return () => {
      // Stop the camera tracks when component unmounts
      const videoElement = videoRef.current;
      if (videoElement && videoElement.srcObject) {
        const stream = videoElement.srcObject;
        const tracks = stream.getTracks();
        tracks.forEach((track) => track.stop());
      }
    };
  }, [isFrontCamera]);

  // Handle QR code scan result
  const handleScan = (data) => {
    if (data && isScanningEnabled) {
      setScannedData(data); // Store scanned data (roll number) in state
      setError(""); // Clear previous error messages
      setSuccess(""); // Clear previous success messages
      setIsScanningEnabled(false); // Disable further scanning while processing
      checkTicket(data); // Check the scanned roll number with backend API
    }
  };

  // Handle error during scanning
  const handleError = (err) => {
    console.error(err);
    setError("An error occurred while scanning. Please try again.");
  };

  // Verify the scanned roll number with backend
  const checkTicket = async (qrData) => {
    try {
      // You should pass both qrData (QR code scanned) and any other necessary data
      const response = await axios.post(
        "http://localhost:5000/api/verify-qrcode", // Your backend API
        { qrData }
      );

      if (response.status === 200 && response.data.status === "success") {
        setSuccess("Successfully scanned! The QR code is valid.");
      } else if (response.data.status === "duplicate") {
        setError("Duplicate ticket detected. This QR has already been scanned.");
      } else {
        setError("Ticket is invalid or not found.");
      }
    } catch (err) {
      console.error("Error verifying ticket: ", err);
      setError("Failed to verify the ticket. Please try again.");
    }
  };

  // Reset the scanner for the next scan
  const resetScanner = () => {
    setScannedData(null);
    setIsScanningEnabled(true); // Enable scanning again
  };

  const handleBack = () => {
    navigate("/admin/dashboard"); // Navigate back to dashboard
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center py-12 px-6">
      <div className="w-full max-w-md bg-white p-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold text-center text-gray-800 mb-6">
          QR Code Scanner
        </h2>

        {/* Camera Feed (Video Element) */}
        <div className="relative mb-4">
          <video
            ref={videoRef}
            autoPlay
            muted
            width="100%"
            height="auto"
            style={{
              borderRadius: "8px",
              border: "2px solid #ddd",
              maxHeight: "400px",
            }}
          />
        </div>

        {/* QR Code Scanner */}
        <QrReader
          delay={500}
          onError={handleError} // Error handling callback
          onResult={handleScan} // Result handling callback
          style={{
            width: "100%",
            height: "auto",
            borderRadius: "8px",
            border: "2px solid #ddd",
            display: "none", // Hide QR reader interface
          }}
          facingMode={isFrontCamera ? "user" : "environment"} // Default to front camera
        />

        {/* Error or Success Message */}
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        {success && <p className="text-green-500 text-center mb-4">{success}</p>}

        {/* Submit Button - Appears after QR Code is Scanned */}
        {success && (
          <button
            onClick={resetScanner}
            className="w-full py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          >
            Next Scan
          </button>
        )}

        {/* Back Button */}
        <button
          onClick={handleBack}
          className="w-full py-3 bg-gray-400 text-white font-semibold rounded-lg hover:bg-gray-500 focus:outline-none focus:ring-2 focus:ring-gray-400 mb-4"
        >
          Back to Dashboard
        </button>

        {/* Switch Camera Button */}
        <button
          onClick={toggleCamera} // Switch camera
          className="w-full py-3 bg-yellow-500 text-white font-semibold rounded-lg hover:bg-yellow-600 focus:outline-none focus:ring-2 focus:ring-yellow-500 mb-4"
        >
          Switch Camera
        </button>
      </div>
    </div>
  );
};

export default QRScanner;
