import { useEffect, useState } from "react";
import { getStudents, approveStudent, rejectStudent } from "../services/api"; // Ensure rejectStudent is implemented

const AdminDashboard = () => {
  const [students, setStudents] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);  // For image preview modal
  const [isModalOpen, setIsModalOpen] = useState(false);     // To control modal visibility
  const token = localStorage.getItem("adminToken");

  useEffect(() => {
    const fetchStudents = async () => {
      const { data } = await getStudents();
      setStudents(data);
    };
    fetchStudents();
  }, []);

  const handleApprove = async (id) => {
    await approveStudent(id, token);
    alert("Student Approved & QR Code Sent!");
    setStudents((prevStudents) =>
      prevStudents.map((student) =>
        student._id === id ? { ...student, approved: true } : student
      )
    );
  };

  const handleReject = async (id) => {
    await rejectStudent(id, token);
    alert("Student Rejected!");
    setStudents((prevStudents) =>
      prevStudents.filter((student) => student._id !== id)
    );
  };

  // Function to handle image preview in modal
  const handleViewIdCard = (studentIdCardUrl) => {
    setSelectedImage(studentIdCardUrl);  // Set the selected image URL
    setIsModalOpen(true);         // Open the modal
  };

  // Function to close the image modal
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedImage(null);
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold">Admin Dashboard</h2>
      <table className="min-w-full bg-white shadow-md rounded mt-4">
        <thead>
          <tr className="bg-blue-600 text-white">
            <th className="p-2">Name</th>
            <th className="p-2">Roll No</th>
            <th className="p-2">Phone No</th>
            <th className="p-2">ID Card</th>
            <th className="p-2">Status</th>
            <th className="p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student._id} className="border-b">
              <td className="p-2">{student.name}</td>
              <td className="p-2">{student.rollNo}</td>
              <td className="p-2">{student.phone || "N/A"}</td>
              <td className="p-2">
                {student.studentIdCard ? (
                  <button
                    onClick={() => handleViewIdCard(student.studentIdCard)}
                    className="text-blue-500 hover:underline"
                  >
                    View ID Card
                  </button>
                ) : (
                  "No ID Card"
                )}
              </td>
              <td className="p-2">{student.approved ? "Approved" : "Pending"}</td>
              <td className="p-2">
                {!student.approved ? (
                  <>
                    <button
                      onClick={() => handleApprove(student._id)}
                      className="bg-green-500 text-white p-1 rounded mr-2"
                    >
                      Approve
                    </button>
                    <button
                      onClick={() => handleReject(student._id)}
                      className="bg-red-500 text-white p-1 rounded"
                    >
                      Reject
                    </button>
                  </>
                ) : (
                  "Approved"
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal to display the ID card image */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-4 rounded-md">
            <button
              onClick={handleCloseModal}
              className="absolute top-0 right-0 p-2 bg-red-500 text-white rounded-full"
            >
              X
            </button>
            <div className="flex justify-center">
              <img src={selectedImage} alt="ID Card" className="max-w-full max-h-[80vh]" />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
