import axios from "axios";

// Create an instance of axios with the base URL
const API = axios.create({ baseURL: "http://localhost:5000/api" });

// Login Admin
export const loginAdmin = (data) => API.post("/admin/login", data);

// Get List of Students
export const getStudents = async () => {
  try {
    const { data } = await API.get("/students");
    return data;
  } catch (error) {
    console.error("Error fetching students:", error);
    throw error; // Re-throw error to be handled in the component
  }
};

// Register a Student
export const registerStudent = (data) => API.post("/students/register", data);

// Approve a Student
export const approveStudent = async (id, token) => {
  try {
    const { data } = await API.put(`/admin/approve/${id}`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });
    return data; // Return the response data
  } catch (error) {
    console.error("Error approving student:", error);
    throw error; // Re-throw error to be handled in the component
  }
};

// Reject a Student
export const rejectStudent = async (id, token) => {
  try {
    const { data } = await API.put(`/admin/reject/${id}`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });
    return data; // Return the response data
  } catch (error) {
    console.error("Error rejecting student:", error);
    throw error; // Re-throw error to be handled in the component
  }
};
