import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5000/api" });

// ‚úÖ Add an interceptor to attach the Authorization token to every request (except login)
API.interceptors.request.use(
  (config) => {
    if (config.url !== "/admin/login") {
      const token = localStorage.getItem("adminToken"); // Get token from localStorage
      if (token) {
        config.headers["Authorization"] = `Bearer ${token}`; // Attach token if available
      }
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// ‚úÖ Login Admin
export const loginAdmin = async (data) => {
  try {
    console.log("üîπ Sending login request with data:", data);
    const response = await API.post("/admin/login", data);
    console.log("‚úÖ Login API response:", response);
    if (!response.data || !response.data.token) {
      throw new Error("Invalid response structure from server");
    }
    localStorage.setItem("adminToken", response.data.token); // Store token in localStorage
    return response.data;
  } catch (error) {
    console.error("‚ùå Login error:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Get List of Students
export const getStudents = async () => {
  try {
    const response = await API.get("/students");
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("‚ùå Error fetching students:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Register a Student (Ensures multipart/form-data handling)
export const registerStudent = async (data) => {
  try {
    const response = await API.post("/students/register", data, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("‚ùå Error registering student:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Approve a Student
export const approveStudent = async (id) => {
  try {
    const response = await API.put(`/admin/approve/${id}`);
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("‚ùå Error approving student:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Reject a Student
export const rejectStudent = async (id) => {
  try {
    const response = await API.put(`/admin/reject/${id}`);
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("‚ùå Error rejecting student:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Verify QR Code
export const verifyQRCode = async (qrData, rollNo) => {
  try {
    const response = await API.post("/verify-qrcode", { qrData, rollNo });
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("‚ùå Error verifying QR code:", error.response?.data || error.message);
    throw error;
  }
};
