import React, { useState } from "react";
import { TextField, Button, CircularProgress } from "@mui/material";
import axios from "axios";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    rollNo: "",
    branch: "",
    email: "",
    phone: "",
    studentID: null,
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, studentID: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    try {
      await axios.post("http://localhost:5000/api/students/apply", formDataToSend);
      alert("Application submitted successfully!");
      setFormData({ name: "", rollNo: "", branch: "", email: "", phone: "", studentID: null });
    } catch (error) {
      console.error(error);
      alert("Error submitting application");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-center mb-6">Ticket Booking</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <TextField fullWidth label="Name" name="name" value={formData.name} onChange={handleChange} required />
          <TextField fullWidth label="Roll No" name="rollNo" value={formData.rollNo} onChange={handleChange} required />
          <TextField fullWidth label="Branch" name="branch" value={formData.branch} onChange={handleChange} required />
          <TextField fullWidth type="email" label="Email" name="email" value={formData.email} onChange={handleChange} required />
          <TextField fullWidth label="Phone" name="phone" value={formData.phone} onChange={handleChange} required />
          <input type="file" accept="image/*" onChange={handleFileChange} required />
          <Button type="submit" variant="contained" color="primary" fullWidth disabled={loading}>
            {loading ? <CircularProgress size={24} /> : "Submit"}
          </Button>
        </form>
      </div>
    </div>
  );
};

export default Register;
