import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { QrReader } from "react-qr-reader"; // ✅ Correct QR scanner library
import axios from "axios";

const QRScanner = () => {
  const [message, setMessage] = useState(null);
  const [scanning, setScanning] = useState(true);
  const [cameraFacing, setCameraFacing] = useState("environment"); // ✅ Default to back camera
  const [cameraError, setCameraError] = useState(false);
  const navigate = useNavigate();

  // ✅ Request Camera Permission on Every Access
  useEffect(() => {
    const requestCameraPermission = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (stream) {
          setCameraError(false); // ✅ Camera access granted
        }
      } catch (error) {
        setCameraError(true); // ✅ Camera access denied
      }
    };

    requestCameraPermission();
  }, []);

  // ✅ Handle QR Code Scan
  const handleScan = async (result) => {
    if (result?.text && scanning) {
      const qrCode = result.text;
      setScanning(false); // Pause scanning while verifying

      try {
        const response = await axios.post("http://localhost:5000/api/admin/verify", { qrCode });

        setMessage({ type: "success", text: response.data.message });

        setTimeout(() => {
          setScanning(true); // Resume scanning after 3 seconds
        }, 3000);
      } catch (error) {
        setMessage({
          type: "error",
          text: error.response?.data?.message || "Error verifying QR code!",
        });

        setTimeout(() => {
          setScanning(true);
        }, 3000);
      }
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <div className="bg-white shadow-lg p-6 rounded-lg w-full max-w-md">
        <h2 className="text-2xl font-bold text-blue-600 mb-4 text-center">QR Code Scanner</h2>

        {/* ✅ Camera Error Message */}
        {cameraError && (
          <p className="p-3 bg-red-500 text-white text-center rounded-md">
            Camera access denied! Please enable camera permissions in your browser settings.
          </p>
        )}

        {/* ✅ Status Message */}
        {message && (
          <p
            className={`p-3 text-center rounded-md text-white ${
              message.type === "error" ? "bg-red-500" : "bg-green-500"
            }`}
          >
            {message.text}
          </p>
        )}

        {/* ✅ QR Scanner */}
        <div className="mt-4 bg-gray-200 rounded-md overflow-hidden">
          {!cameraError && (
            <QrReader
              constraints={{
                video: { facingMode: cameraFacing }, // ✅ Properly set video constraints
              }}
              onResult={handleScan}
              scanDelay={500}
              containerStyle={{ width: "100%" }} // ✅ Ensures scanner is visible
              videoStyle={{ width: "100%", height: "300px", objectFit: "cover" }} // ✅ Makes sure video is displayed correctly
            />
          )}
        </div>

        {/* ✅ Buttons */}
        <div className="flex flex-col gap-3 mt-4">
          {!cameraError && (
            <button
              onClick={() => setCameraFacing((prev) => (prev === "environment" ? "user" : "environment"))}
              className="bg-yellow-500 text-white p-2 rounded-md text-center shadow-md hover:bg-yellow-600 transition-all"
            >
              Switch Camera
            </button>
          )}

          <button
            onClick={() => navigate("/admin/dashboard")}
            className="bg-gray-600 text-white p-2 rounded-md text-center shadow-md hover:bg-gray-700 transition-all"
          >
            Go Back to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default QRScanner;
