import axios from "axios";

// Set baseURL dynamically for different environments (development or production)
const baseURL = process.env.REACT_APP_API_URL || "http://localhost:5000/api"; // Use env variable for flexibility

const API = axios.create({ baseURL });

// Add an interceptor to attach the Authorization token to every request (except login)
API.interceptors.request.use(
  (config) => {
    if (config.url !== "/admin/login") {
      const token = localStorage.getItem("adminToken"); // Get token from localStorage
      if (token) {
        config.headers["Authorization"] = `Bearer ${token}`; // Attach token if available
      }
    }
    return config;
  },
  (error) => {
    console.error("âŒ Request Interceptor Error:", error.message);
    return Promise.reject(error);
  }
);

// Add a response interceptor to handle token expiration (e.g., 401 Unauthorized)
API.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      // Handle token expiry or unauthorized error
      console.warn("âŒ Token expired or invalid. Redirecting to login...");
      localStorage.removeItem("adminToken"); // Remove token from localStorage
      window.location.href = "/admin/login"; // Redirect to login page
    }
    return Promise.reject(error);
  }
);

// âœ… Login Admin - Ensures response structure is valid
export const loginAdmin = async (data) => {
  try {
    console.log("ðŸ”¹ Sending login request with data:", data);
    const response = await API.post("/admin/login", data);

    console.log("âœ… Login API response:", response);

    if (!response.data || !response.data.token) {
      throw new Error("Invalid response structure from server");
    }

    localStorage.setItem("adminToken", response.data.token); // Store token in localStorage
    return response.data;
  } catch (error) {
    console.error("âŒ Login error:", error.response?.data || error.message);
    throw error;
  }
};

// âœ… Get List of Students - Ensures correct API response
export const getStudents = async () => {
  try {
    const response = await API.get("/students");
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("âŒ Error fetching students:", error.response?.data || error.message);
    throw error;
  }
};

// âœ… Register a Student
export const registerStudent = async (data) => {
  try {
    const response = await API.post("/students/register", data);

    // Log the full response for debugging
    console.log("Full response from server:", response);

    // Check if the response contains the 'message' key in 'data'
    if (!response.data || !response.data.message) {
      throw new Error("Invalid response structure from server.");
    }

    // Handle success case
    return response.data; // Return the data received from server (e.g., message and student)
  } catch (error) {
    console.error("âŒ Error registering student:", error.response?.data || error.message);
    throw error; // Throw error so it can be caught by the frontend form
  }
};

// âœ… Approve a Student
export const approveStudent = async (id) => {
  try {
    const response = await API.put(`/admin/approve/${id}`);
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("âŒ Error approving student:", error.response?.data || error.message);
    throw error;
  }
};

// âœ… Reject a Student
export const rejectStudent = async (id) => {
  try {
    const response = await API.put(`/admin/reject/${id}`);
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("âŒ Error rejecting student:", error.response?.data || error.message);
    throw error;
  }
};

// âœ… Get Student by ID (useful for Admin Dashboard to get individual student details)
export const getStudentById = async (id) => {
  try {
    const response = await API.get(`/students/${id}`);
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    console.error("âŒ Error fetching student by ID:", error.response?.data || error.message);
    throw error;
  }
};

// âœ… Retry logic (optional): Add retry functionality for failed requests, e.g., 502, 503 errors
const retryRequest = async (func, retries = 3, delay = 1000) => {
  try {
    return await func();
  } catch (error) {
    if (retries <= 0) throw error;
    console.warn(`âŒ Retrying request... Attempts left: ${retries}`);
    await new Promise(resolve => setTimeout(resolve, delay));
    return retryRequest(func, retries - 1, delay);
  }
};

// Example: Using retry logic for a critical request (like fetching data)
export const fetchDataWithRetry = async () => {
  return retryRequest(() => API.get("/some-critical-endpoint"));
};
