import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { BrowserMultiFormatReader } from "@zxing/browser"; // Updated to use @zxing/browser

const QRScanner = () => {
  const [error, setError] = useState(""); // Error message state
  const [success, setSuccess] = useState(""); // Success message state
  const [scannedData, setScannedData] = useState(null); // Store scanned QR data
  const [isScanningEnabled, setIsScanningEnabled] = useState(true); // Control scanning state
  const [isFrontCamera, setIsFrontCamera] = useState(false); // Handle front/back camera
  const navigate = useNavigate();
  
  // Use Ref to hold video element
  const videoRef = useRef(null);
  let codeReader = new BrowserMultiFormatReader(); // ZXing QR scanner instance

  // Start the camera and QR scanner
  const startCamera = async (facingMode) => {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices();
      const cameras = devices.filter((device) => device.kind === "videoinput");

      // Pick the correct camera
      const cameraId = facingMode === "user" ? cameras[0]?.deviceId : cameras[1]?.deviceId || cameras[0]?.deviceId;

      if (cameraId) {
        await codeReader.decodeFromVideoDevice(cameraId, videoRef.current, (result, err) => {
          if (result && isScanningEnabled) {
            handleScan(result.getText());
          }
          if (err) {
            setError(err.message);
          }
        });
      } else {
        setError("No camera found");
      }
    } catch (err) {
      setError("Unable to access camera. Please check your permissions.");
    }
  };

  // Switch the camera between front and back
  const toggleCamera = () => {
    setIsFrontCamera((prev) => !prev);
  };

  // Start camera on component mount
  useEffect(() => {
    startCamera(isFrontCamera ? "user" : "environment");

    return () => {
      // Stop the camera tracks when component unmounts
      if (videoRef.current?.srcObject) {
        const stream = videoRef.current.srcObject;
        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [isFrontCamera]);

  // Handle QR code scan result
  const handleScan = (data) => {
    if (data && isScanningEnabled) {
      setScannedData(data);
      setError("");
      setSuccess("");
      setIsScanningEnabled(false);
      checkTicket(data);
    }
  };

  // Verify the scanned roll number with backend
  const checkTicket = async (qrData) => {
    try {
      const response = await axios.post("http://localhost:5000/api/verify-qrcode", { qrData });

      if (response.status === 200 && response.data.status === "success") {
        setSuccess("Successfully scanned! The QR code is valid.");
      } else if (response.data.status === "duplicate") {
        setError("Duplicate ticket detected. This QR has already been scanned.");
      } else {
        setError("Ticket is invalid or not found.");
      }
    } catch (err) {
      setError("Failed to verify the ticket. Please try again.");
    }
  };

  // Reset the scanner for the next scan
  const resetScanner = () => {
    setScannedData(null);
    setIsScanningEnabled(true);
  };

  const handleBack = () => {
    navigate("/admin/dashboard");
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center py-12 px-6">
      <div className="w-full max-w-md bg-white p-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold text-center text-gray-800 mb-6">
          QR Code Scanner
        </h2>

        {/* Camera Feed (Video Element) */}
        <div className="relative mb-4">
          <video
            ref={videoRef}
            autoPlay
            muted
            width="100%"
            height="auto"
            style={{
              borderRadius: "8px",
              border: "2px solid #ddd",
              maxHeight: "400px",
            }}
          />
        </div>

        {/* Error or Success Message */}
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        {success && <p className="text-green-500 text-center mb-4">{success}</p>}

        {/* Submit Button - Appears after QR Code is Scanned */}
        {success && (
          <button
            onClick={resetScanner}
            className="w-full py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          >
            Next Scan
          </button>
        )}

        {/* Back Button */}
        <button
          onClick={handleBack}
          className="w-full py-3 bg-gray-400 text-white font-semibold rounded-lg hover:bg-gray-500 focus:outline-none focus:ring-2 focus:ring-gray-400 mb-4"
        >
          Back to Dashboard
        </button>

        {/* Switch Camera Button */}
        <button
          onClick={toggleCamera}
          className="w-full py-3 bg-yellow-500 text-white font-semibold rounded-lg hover:bg-yellow-600 focus:outline-none focus:ring-2 focus:ring-yellow-500 mb-4"
        >
          Switch Camera
        </button>
      </div>
    </div>
  );
};

export default QRScanner;
