import { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import QRScanner from 'qr-scanner'; // Import the QRScanner library
import axios from 'axios';

const QRScannerComponent = () => {
  const [message, setMessage] = useState(null);
  const [cameraFacing, setCameraFacing] = useState('environment'); // Default to back camera
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const qrScannerRef = useRef(null); // To hold QRScanner instance

  useEffect(() => {
    // Initialize QRScanner
    qrScannerRef.current = new QRScanner(videoRef.current, handleScan);

    // Set up camera constraints
    const constraints = {
      video: {
        facingMode: cameraFacing, // Default facing mode
        width: { ideal: 1280 },
        height: { ideal: 720 },
      },
    };

    // Access the camera and start the video feed
    navigator.mediaDevices
      .getUserMedia(constraints)
      .then((stream) => {
        videoRef.current.srcObject = stream;
        qrScannerRef.current.start(); // Start scanning
      })
      .catch((error) => {
        setMessage({ type: 'error', text: 'Camera access denied or unavailable!' });
        console.error(error);
      });

    // Cleanup: Stop the video stream when component unmounts
    return () => {
      const stream = videoRef.current.srcObject;
      const tracks = stream?.getTracks();
      tracks?.forEach(track => track.stop()); // Stop the camera feed
    };
  }, [cameraFacing]);

  // Handle QR Code Scan
  const handleScan = async (qrCode) => {
    if (qrCode) {
      console.log('Scanned QR Code:', qrCode);

      try {
        const response = await axios.post('http://localhost:5000/api/admin/verify', { qrCode });

        setMessage({ type: 'success', text: response.data.message });

        // Auto-clear success message after 3 seconds
        setTimeout(() => {
          setMessage(null);
        }, 3000);
      } catch (error) {
        setMessage({
          type: 'error',
          text: error.response?.data?.message || 'Error verifying QR code!',
        });

        // Auto-clear error message after 3 seconds
        setTimeout(() => {
          setMessage(null);
        }, 3000);
      }
    }
  };

  // Handle Camera Errors
  const handleError = (error) => {
    console.error('Camera Error:', error);
    setMessage({ type: 'error', text: 'Camera access denied or unavailable!' });

    // Auto-clear error message after 3 seconds
    setTimeout(() => {
      setMessage(null);
    }, 3000);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      {/* Modal Container */}
      <div className="bg-white shadow-lg p-6 rounded-lg w-full max-w-md relative">
        <h2 className="text-2xl font-bold text-blue-600 mb-4 text-center">QR Code Scanner</h2>

        {/* Status Message */}
        {message && (
          <p
            className={`p-3 text-center rounded-md text-white ${
              message.type === 'error' ? 'bg-red-500' : 'bg-green-500'
            }`}
          >
            {message.text}
          </p>
        )}

        {/* QR Scanner Video Feed */}
        <div
          className="scanner-container mt-4 bg-gray-200 rounded-md overflow-hidden"
          style={{ position: 'relative', width: '100%', height: '300px' }}
        >
          {/* Video element to show the camera feed */}
          <video
            ref={videoRef}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
            }}
            autoPlay
            muted
          ></video>
        </div>

        {/* Switch Camera and Navigation Buttons */}
        <div className="flex flex-col gap-3 mt-4">
          <button
            onClick={() => setCameraFacing((prev) => (prev === 'environment' ? 'user' : 'environment'))}
            className="bg-yellow-500 text-white p-2 rounded-md text-center shadow-md hover:bg-yellow-600 transition-all"
          >
            Switch Camera
          </button>

          <button
            onClick={() => navigate('/admin/dashboard')}
            className="bg-gray-600 text-white p-2 rounded-md text-center shadow-md hover:bg-gray-700 transition-all"
          >
            Go Back to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default QRScannerComponent;
