import { useState } from "react";
import { registerStudent } from "../services/api";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    rollNo: "",
    branch: "",
    email: "",
    phone: "",
    studentIdCard: null,
  });

  const [message, setMessage] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, studentIdCard: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);
  
    const formDataObj = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      formDataObj.append(key, value);
    });
  
    try {
      const response = await registerStudent(formDataObj);
  
      // Handle success if the backend response is structured correctly
      if (response.status === "success") {
        setMessage({ type: "success", text: response.message || "Ticket booked successfully." });
      } else {
        // If backend returns error status, show the message
        setMessage({ type: "error", text: response.message || "Error booking the ticket." });
      }
    } catch (error) {
      // Log and display the error message from the catch block
      console.error("❌ Error during registration:", error);
      setMessage({
        type: "error",
        text: error.response?.data?.message || "Error booking the ticket. Please try again later."
      });
    }
  };
  
  
  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-lg mt-10">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">Give Details to Book your Ticket</h2>

      {message && (
        <p className={`p-3 text-white ${message.type === "error" ? "bg-red-500" : "bg-green-500"}`}>
          {message.text}
        </p>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Name"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="rollNo"
          placeholder="Roll Number"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="branch"
          placeholder="Branch"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone Number"
          onChange={handleChange}
          className="w-full p-2 border"
          required
        />
        <input
          type="file"
          name="studentIdCard"
          accept="image/*"
          onChange={handleFileChange}
          className="w-full p-2 border"
          required
        />
        <button type="submit" className="bg-blue-600 text-white p-2 w-full">
          Book Now
        </button>
      </form>
    </div>
  );
};

export default Register;
