import axios from 'axios';

// Set baseURL dynamically for different environments (development or production)
const baseURL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// Create axios instance
const API = axios.create({ baseURL });

// Centralized error handler
const handleApiError = (error) => {
  if (error.response) {
    console.error("API Error:", error.response?.data || error.message || error);
  } else {
    console.error("Error:", error.message || error);
  }
  return Promise.reject(error);  // Make sure to propagate the error
};

// Request interceptor for attaching Authorization token
API.interceptors.request.use(
  (config) => {
    if (config.url !== "/admin/login") {
      const token = localStorage.getItem("adminToken");
      if (token) {
        config.headers["Authorization"] = `Bearer ${token}`;
      }
    }
    return config;
  },
  handleApiError
);

// Response interceptor to handle expired token
API.interceptors.response.use(
  (response) => response,
  (error) => {
    const status = error.response?.status;
    if (status === 401 || error.response?.data?.message === "Token expired") {
      console.warn("❌ Token expired or invalid. Redirecting to login...");
      localStorage.removeItem("adminToken");
      window.location.href = "/admin/login";
    }
    return Promise.reject(error);
  }
);

// ✅ Register a Student
export const registerStudent = async (data) => {
  try {
    const response = await API.post("/students/register", data);
    if (!response.data || !response.data.message) {
      throw new Error("Invalid response structure from server.");
    }
    return response.data;
  } catch (error) {
    handleApiError(error);
    throw error;
  }
};

// ✅ Get List of Students
export const getStudents = async () => {
  try {
    const response = await API.get("/students");
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    handleApiError(error);
    throw error;
  }
};

// ✅ Approve a Student
export const approveStudent = async (id) => {
  try {
    const response = await API.put(`/admin/approve/${id}`);
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    handleApiError(error);
    throw error;
  }
};

// ✅ Reject a Student
export const rejectStudent = async (id) => {
  try {
    const response = await API.put(`/admin/reject/${id}`);
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    handleApiError(error);
    throw error;
  }
};

// ✅ Verify QR Code
export const verifyQRCode = async (qrCode) => {
  try {
    const response = await API.post("/admin/verify", { qrCode });
    if (!response.data) throw new Error("Invalid response from server");
    return response.data;
  } catch (error) {
    handleApiError(error);
    throw error;
  }
};

// Utility: Get Auth Token from localStorage with Expiry Check
const getAuthToken = () => {
  const storedToken = JSON.parse(localStorage.getItem("adminToken"));
  if (!storedToken || new Date().getTime() > storedToken.expiryTime) {
    localStorage.removeItem("adminToken");
    return null;
  }
  return storedToken.token;
};

// Utility: Set Auth Token with Expiry Time
const setAuthToken = (token) => {
  const expiryTime = new Date().getTime() + 60 * 60 * 1000; // 1 hour expiry
  localStorage.setItem("adminToken", JSON.stringify({ token, expiryTime }));
};

export { 
 
 


  setAuthToken, 
  getAuthToken 
};
