import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getAllStudents, approveStudent, rejectStudent, verifyQRCode } from "../services/api"; // Correct API imports
import { FiCamera } from "react-icons/fi"; // Camera icon for QR Scanner

const AdminDashboard = () => {
  const [students, setStudents] = useState([]); // State to store students
  const [selectedIdCard, setSelectedIdCard] = useState(null); // State to store selected ID card
  const [error, setError] = useState(""); // Error handling state
  const [showModal, setShowModal] = useState(false); // Modal visibility state
  const [scannedQRCodeData, setScannedQRCodeData] = useState(""); // State for scanned QR data
  const navigate = useNavigate(); // To navigate programmatically

  // Check if admin is logged in by checking the token in localStorage
  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) {
      navigate("/admin/login"); // If not logged in, redirect to login
    }
  }, [navigate]);

  // Fetch all students from the API
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await getAllStudents(); // Fetch students from the correct API function
        if (Array.isArray(response)) {
          setStudents(response); // Update the students state with the fetched data
        } else {
          throw new Error("Invalid response format from server");
        }
      } catch (err) {
        console.error("Error fetching students:", err);
        setError("Failed to load students. Please try again.");
      }
    };

    fetchStudents(); // Fetch students when the component mounts
  }, []);

  // Approve a student
  const handleApprove = async (id) => {
    try {
      await approveStudent(id); // Call approve student API function
      alert("Student Approved & QR Code Sent!");
      setStudents((prevStudents) =>
        prevStudents.map((student) =>
          student._id === id ? { ...student, approved: true, rejected: false } : student
        )
      );
    } catch (error) {
      console.error("Error approving student:", error);
      alert("An error occurred while approving the student.");
    }
  };

  // Reject a student
  const handleReject = async (id) => {
    try {
      await rejectStudent(id); // Call reject student API function
      alert("Student Rejected!");
      setStudents((prevStudents) =>
        prevStudents.map((student) =>
          student._id === id ? { ...student, approved: false, rejected: true } : student
        )
      );
    } catch (error) {
      console.error("Error rejecting student:", error);
      alert("An error occurred while rejecting the student.");
    }
  };

  // Logout the admin
  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    navigate("/admin/login"); // Redirect to login page
  };

  // Open the QR scanner page
  const handleScanQR = () => {
    navigate("/admin/scan");
  };

  // Handle viewing ID card
  const handleViewIdCard = (idCard) => {
    setSelectedIdCard(idCard);
    setShowModal(true);
  };

  // Function to check if the ID Card is a base64 string
  const isBase64 = (str) => {
    return /^data:image\/(jpeg|png|jpg);base64,/.test(str);
  };

  // Generate the full URL for an image
  const getImageUrl = (imagePath) => {
    if (imagePath) {
      const baseURL = "http://localhost:5000"; // Update to your server URL
      return imagePath.startsWith("/") ? baseURL + imagePath : imagePath;
    }
    return "";
  };

  // Handle QR code verification
  const handleQRCodeVerification = async () => {
    try {
      if (!scannedQRCodeData) {
        alert("No QR Code data scanned.");
        return;
      }

      const response = await verifyQRCode(scannedQRCodeData);

      if (response.success) {
        alert(response.message); // Show success message from backend
      } else {
        alert(response.message || "QR Code verification failed.");
      }
    } catch (error) {
      console.error("Error verifying QR code:", error);
      alert("An error occurred while verifying the QR code.");
    }
  };

  return (
    <div className="flex flex-col lg:flex-row min-h-screen bg-gray-100">
      {/* Sidebar (Left) - QR Scanner Section */}
      <div className="lg:w-1/4 bg-white shadow-md p-6 flex flex-col items-center justify-center">
        <h3 className="text-xl font-bold text-gray-700 mb-4">QR Code Scanner</h3>
        <button
          onClick={handleScanQR}
          className="bg-blue-600 text-white flex items-center gap-2 px-4 py-2 rounded-lg shadow-md hover:bg-blue-700 transition-all"
        >
          <FiCamera className="text-xl" />
          Scan QR Code
        </button>

        {/* Add manual QR Code data input and verification */}
        <input
          type="text"
          value={scannedQRCodeData}
          onChange={(e) => setScannedQRCodeData(e.target.value)}
          placeholder="Enter QR Code data"
          className="mt-4 p-2 border rounded"
        />
        <button
          onClick={handleQRCodeVerification}
          className="bg-green-600 text-white px-4 py-2 rounded mt-2"
        >
          Verify QR Code
        </button>
      </div>

      {/* Main Dashboard Content */}
      <div className="flex-1 p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-bold">Admin Dashboard</h2>
          <button
            onClick={handleLogout}
            className="bg-red-600 text-white px-4 py-2 rounded"
          >
            Logout
          </button>
        </div>

        {error && <p className="text-red-500 text-center">{error}</p>}

        <div className="overflow-x-auto">
          <table className="min-w-full bg-white shadow-md rounded">
            <thead>
              <tr className="bg-blue-600 text-white">
                <th className="p-2">Name</th>
                <th className="p-2">Roll No</th>
                <th className="p-2">Phone</th>
                <th className="p-2">ID Card</th>
                <th className="p-2">Status</th>
                <th className="p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {students.length > 0 ? (
                students.map((student) => (
                  <tr key={student._id} className="border-b">
                    <td className="p-2">{student.name}</td>
                    <td className="p-2">{student.rollNo}</td>
                    <td className="p-2">{student.phone}</td>
                    <td className="p-2">
                      {student.studentIdCard ? (
                        <button
                          onClick={() => handleViewIdCard(student.studentIdCard)}
                          className="text-blue-500 hover:underline"
                        >
                          View ID Card
                        </button>
                      ) : (
                        <span>No ID Card</span>
                      )}
                    </td>
                    <td className="p-2">
                      {student.approved ? (
                        <span className="text-green-600 font-bold">Approved</span>
                      ) : student.rejected ? (
                        <span className="text-red-600 font-bold">Rejected</span>
                      ) : (
                        <span className="text-yellow-500 font-bold">Pending</span>
                      )}
                    </td>
                    <td className="p-2">
                      {!student.approved && !student.rejected ? (
                        <>
                          <button
                            onClick={() => handleApprove(student._id)}
                            className="bg-green-500 text-white p-1 rounded mr-2"
                          >
                            Approve
                          </button>
                          <button
                            onClick={() => handleReject(student._id)}
                            className="bg-red-500 text-white p-1 rounded"
                          >
                            Reject
                          </button>
                        </>
                      ) : (
                        <span>-</span>
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="p-4 text-center text-gray-500">
                    No students found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal to view ID Card */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
          <div className="bg-white p-6 rounded-lg max-w-md w-full">
            <h3 className="text-xl font-bold mb-4">Student ID Card</h3>
            {selectedIdCard ? (
              <img
                src={getImageUrl(selectedIdCard)} // Use the helper function to prepend the full URL
                alt="Student ID Card"
                className="w-full h-auto object-cover mb-4"
              />
            ) : (
              <p>No ID Card Available</p>  // Fallback message
            )}
            <button
              onClick={() => setShowModal(false)}
              className="bg-red-500 text-white px-4 py-2 rounded-lg"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
