import axios from "axios";

const BASE_URL = "http://localhost:5000"; // üîπ Change this to your backend URL
const API = axios.create({ baseURL: `${BASE_URL}/api` });

// ‚úÖ Attach Authorization Token to Every Request (Except Login)
API.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("adminToken");
    if (token && config.url !== "/admin/login") {
      config.headers["Authorization"] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// ‚úÖ Login Admin
export const loginAdmin = async (data) => {
  try {
    console.log("üîπ Sending login request with data:", data);
    const response = await API.post("/admin/login", data);

    console.log("‚úÖ Login API response:", response);

    if (!response.data || !response.data.token) {
      throw new Error("Invalid response structure from server");
    }

    localStorage.setItem("adminToken", response.data.token);
    return response.data;
  } catch (error) {
    console.error("‚ùå Login error:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Get List of Students (Fixes studentIdCard Path)
export const getStudents = async () => {
  try {
    const response = await API.get("/students");

    if (!response.data) {
      throw new Error("Invalid response from server");
    }

    // ‚úÖ Convert relative ID card URLs to full URLs
    const students = response.data.map((student) => ({
      ...student,
      studentIdCard: student.studentIdCard?.startsWith("http")
        ? student.studentIdCard
        : `${BASE_URL}/${student.studentIdCard}`,
    }));

    console.log("‚úÖ Students fetched:", students);
    return students;
  } catch (error) {
    console.error("‚ùå Error fetching students:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Register a Student
export const registerStudent = async (data) => {
  try {
    const response = await API.post("/students/register", data);

    if (!response.data) {
      throw new Error("Invalid response from server");
    }

    return response.data;
  } catch (error) {
    console.error("‚ùå Error registering student:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Approve a Student
export const approveStudent = async (id) => {
  try {
    const response = await API.put(`/admin/approve/${id}`);

    if (!response.data) {
      throw new Error("Invalid response from server");
    }

    return response.data;
  } catch (error) {
    console.error("‚ùå Error approving student:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Reject a Student
export const rejectStudent = async (id) => {
  try {
    const response = await API.put(`/admin/reject/${id}`);

    if (!response.data) {
      throw new Error("Invalid response from server");
    }

    return response.data;
  } catch (error) {
    console.error("‚ùå Error rejecting student:", error.response?.data || error.message);
    throw error;
  }
};
